-- 创建地铁线路表
CREATE TABLE beijing_subway (
  station_id    INT         NOT NULL PRIMARY KEY auto_increment comment '主键',
  line_name     VARCHAR(20) NOT NULL                            comment '线路名',
  station_name  VARCHAR(50) NOT NULL                            comment '站点名',
  next_station  VARCHAR(50) NOT NULL                            comment '下一站',
  direction     VARCHAR(50) NOT NULL                            comment '线路方向'
) comment '北京地铁线路表';

